"use strict";
import React, { Component } from "react";
import API from "../utils/API.js";
import Header from '../components/Header/Header.js';
import Footer from "../components/Footer/Footer.js";
import Results from "../pages/Results";
import Search from "../components/Search/Search.js";
import Industry from "../components/Industry/Industry.js";

class Home extends Component {

    state = {
        keyword: "",
        reviews: [],
        location: ""
    }
    handleInputChange = event => {
        event.preventDefault();
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    };
    handleSubmit = event => {
        event.preventDefault();
        API.Search(this.state.keyword)
    };
    handlePreset = event => {
        const {id} = event.target;
        event.preventDefault();
        this.setState({
            "keyword": id
        });
        API.Search(this.state.keyword).then(data =>{
            this.setState({ "reviews": data.reviews});
        });
    };
    constructor(props) {
        super(props);
        this.state = {
            videoURL: 'https://fpdl.vimeocdn.com/vimeo-prod-skyfire-std-us/01/2566/11/287831458/1087833265.mp4?token=1543892716-0x38e1b989c2ad4cf0c9466c033acbffa6ce001916'
        }
    }

    render() {
        return (
        <div>

            <Header/>
            <div className="columns">
                <div id="search" className="hero-body has-text-centered">
                    <div className="columns is-mobile is-centered">
                        <div className="column is-half is-narrow">
                        <div className="field has-addons">
                            <div className="control is-expanded">
                            <input name="keyword" onChange={this.handleInputChange} className="input" type="text" placeholder="Find a business" id="search-bar"/>
                            </div>
                            <div className="control">
                            <button onClick={this.handlesubmit} className="button is-info" id="btn-info" style={{border: "none"}}>
                                Search
                            </button>
                            </div>
                        </div>
                        </div>
                    </div>
                </div>
            </div>
                {this.state.reviews.length === 0 ? (
                    <div className="wrapper" id="columns">
                        <Industry event={this.handlePreset}/>
                </div> )
                : (<Results reviews={this.state.reviews}/>)}
            <Footer/>
        </div>
        );
    }
}
export default Home;
