"use strict";
import React, { Component } from "react";
import API from "../utils/API.js";
import Header from '../components/Header/Header.js';
import Footer from "../components/Footer/Footer.js";
import Collapsible from "react-collapsible";
import Results from "../pages/Results";
import Search from "../components/Search/Search.js";
import Industry from "../components/Industry/Industry.js";

class Home extends Component {

    state = {
        keyword: "",
        reviews: [],
        location: ""
    }
    handleInputChange = event => {
        event.preventDefault();
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    };
    handleSubmit = event => {
        event.preventDefault();
        API.query(this.state.keyword).then(data =>{
            this.setState({ "reviews": data.reviews});
        });
    };
    handlePreset = event => {
        const {id} = event.target;
        event.preventDefault();
        this.setState({
            "keyword": id
        });
        API.query(this.state.keyword).then(data =>{
            this.setState({ "reviews": data.reviews.Results});
        });
    };
    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         videoURL: 'https://fpdl.vimeocdn.com/vimeo-prod-skyfire-std-us/01/2566/11/287831458/1087833265.mp4?token=1543892716-0x38e1b989c2ad4cf0c9466c033acbffa6ce001916'
            
    //     }
    // }

    render() {
        return (
        <div>

            <Header/>
                <div className="columns">
                    <div id="search" className="hero-body has-text-centered">
                        <div className="columns is-mobile is-centered">
                            <div className="column is-half is-narrow">
                            <div className="field has-addons">
                                <div className="control is-expanded"><input name="keyword" onChange={this.handleInputChange} className="input" type="text" placeholder="Find a business" id="search-bar">{this.state.keyword}</input></div>
                                <div className="control">
                                <button onClick={this.handleSubmit} className="button is-info" id="btn-info" style={{border: "none"}}>Search</button>
                                </div>
                            </div>
                            </div>
                        </div>
                    </div>
                </div>
                {this.state.reviews.length === 0 ? (
                    <div className="wrapper" id="columns">
                        <div id="industry">
                            <div className="tile is-ancestor">
                                <div className="tile is-parent"> 
                                    <article className="tile is-child box" id="auto">
                                    {/* <p className="title"></p> */}
                                    {/* <p className="subtitle" >Auto</p> */}
                                    </article>
                                </div>
                                <div className="tile is-parent">
                                    <article className="tile is-child box" id="food">
                                    {/* <p className="title">Two</p> */}
                                    {/* <p className="subtitle" >Food</p> */}
                                    </article>
                                </div>
                                <div className="tile is-parent">
                                    <article className="tile is-child box" id="retail">
                                    {/* <p className="title">Three</p> */}
                                    {/* <p className="subtitle">Retail</p> */}
                                    </article>
                                </div>
                                <div className="tile is-parent">
                                    <article className="tile is-child box" id="health">
                                    {/* <p className="title">Four</p> */}
                                    {/* <p className="subtitle">Health</p> */}
                                    </article>
                                </div>
                                
                            </div>
                            <div className="tile is-ancestor">
                                <div className="tile is-parent">
                                    <article className="tile is-child box" id="services">
                                    {/* <p className="title">One</p> */}
                                    {/* <p className="subtitle">Services</p> */}
                                    </article>
                                </div>
                                <div className="tile is-parent">
                                    <article className="tile is-child box" id="hotels">
                                    {/* <p className="title">Two</p> */}
                                    {/* <p className="subtitle">Hotels</p> */}
                                    </article>
                                </div>
                                <div className="tile is-parent">
                                    <article className="tile is-child box" id="entertainment">
                                    {/* <p className="title">Three</p> */}
                                    {/* <p className="subtitle">Entertainment</p> */}
                                    </article>
                                </div>
                                <div className="tile is-parent">
                                    <article className="tile is-child box" id=
                                    "shop">
                                    {/* <p className="title">Four</p> */}
                                    {/* <p className="subtitle">Clothing + Beauty</p> */}
                                    </article>
                                </div>
                            </div>
                        </div>
                    </div> 
                ) : this.state.reviews.forEach(place => {return (
                        <div className="section ">
                        <div className="container is-fluid level" id="container">
                            <div className="tile is-ancestor">
                                <div className="tile is-vertical is-10 is-offset-2">
                                    <div className="tile is-parent">
                                        <div className="tile is-child box">
                                            <figure>
                                                <img src="https://fakeimg.pl/250x100/" />
                                            </figure>
                                            <div id="">
                                                <h4>{place.name}</h4>
                                                <p>Phone Number</p>
                                                <p>Open Now: {place.opening_hours.open_now}</p>
                                                <p>{place.formatted_address}</p>
                                                <span>Rating: {place.rating} (REVIEWS:)</span>
                                            </div>
                                            
                                            <Collapsible trigger="View Comments">
                                                <div className="container is-fluid" id="comment">
                                                    <div className="tile is-ancestor">
                                                        <div className="tile is-vertical">
                                                        {place.reviews.forEach(review => { return (
                                                                <div className="tile is-parent" id="ind-reviews">
                                                                    <figure className="image is-70x70">
                                                                        <img src={review.profile_photo_url} className="is-rounded" alt="userpic" />
                                                                    </figure>
                                                                    <article className="tile is-child speech-bubble box comment">
                                                                        <p>{review.author_name}</p>
                                                                        <p>{review.rating}</p>
                                                                        <p>{review.time}</p>
                                                                        <p>{review.text}</p>
                                                                    </article>
                                                                </div>
                                                        )})}
                                                        </div>
                                                    </div>
                                                </div>
                                            </Collapsible>
                                        </div>
                                    </div>
                                    <div className="tile is-parent">
                                        <div className="tile is-child box">
                                            <img src="https://fakeimg.pl/250x100/" />
                                            <h4>Restaurant Name</h4>
                                            <p>Address</p>
                                            <span>Rating and number of reviews</span>
                                        </div>
                                    </div>
                                    <div className="tile is-parent">
                                        <div className="tile is-child box">
                                            <img src="https://fakeimg.pl/250x100/" />
                                            <h4>Restaurant Name</h4>
                                            <p>Address</p>
                                            <span>Rating and number of reviews</span>
                                        </div>
                                    </div>
                                    <div className="tile is-parent">
                                        <div className="tile is-child box">
                                            <img src="https://fakeimg.pl/250x100/" />
                                            <h4>Restaurant Name</h4>
                                            <p>Address</p>
                                            <span>Rating and number of reviews</span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                )})}
            <Footer/>
        </div>
        );
    }
}
export default Home;
